\left[ T \right]_{\beta}^{\gamma} = \begin{bmatrix} 
0 &amp;amp; 1 &amp;amp; 0 \\
0 &amp;amp; 0 &amp;amp; 2 \\
\end{bmatrix},\left[ U \right]_{\gamma}^{\beta} = \begin{bmatrix}
0 &amp;amp; 0 \\
1 &amp;amp; 0 \\
